namespace Pimcore\Model\Document\Editable;

interface IdRewriterInterface
{
    /**
     * Rewrites id from source to target, $idMapping contains
     * array(
     *  "document" => array(
     *      SOURCE_ID => TARGET_ID,
     *      SOURCE_ID => TARGET_ID
     *  ),
     *  "object" => array(...),
     *  "asset" => array(...)
     * )
     *
     */
    public function rewriteIds(array $idMapping): void;
}
